import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/Projects.css";

export default function Projects() {
  const [projects, setProjects] = useState([]);

  // Fetch all available projects
  useEffect(() => {
    axios.get("http://localhost:8080/api/projects")
      .then(res => setProjects(res.data))
      .catch(err => console.error(err));
  }, []);

  // Select a project (does NOT navigate)
  const handleSelect = (project) => {
    const username = localStorage.getItem("username") || "guest";

    axios.post(`http://localhost:8080/api/projects/select`, null, {
      params: { username, projectId: project.id }
    })
    .then(() => {
      alert(`You selected ${project.name}!`);
    })
    .catch(err => console.error(err));
  };

  return (
    <div className="page-container projects-container">
      <h1>Choose Your Next Project</h1>
      <div className="projects-list">
        {projects.map(project => (
          <div key={project.id} className="project-card">
            <h3>{project.name}</h3>
            <p>Price: ${project.pricePerHour}/hr</p>
            <button onClick={() => handleSelect(project)}>
              Select & Start
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
