import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/InvoicePage.css";

export default function InvoicePage() {
  const [completedProjects, setCompletedProjects] = useState([]);
  const username = localStorage.getItem("username") || "guest";

  useEffect(() => {
    // Fetch only the completed projects for the logged-in user
    axios.get(`http://localhost:8081/api/user-projects/completed?username=${username}`)
      .then((res) => {
        setCompletedProjects(res.data);
      })
      .catch((err) => console.error(err));
  }, [username]);

  const handleDownload = (userProjectId) => {
    axios.get(`http://localhost:8081/api/invoices/download/${userProjectId}`, {
      responseType: 'blob' // This is crucial for handling a PDF response
    })
    .then((res) => {
      const fileURL = window.URL.createObjectURL(new Blob([res.data]));
      const fileLink = document.createElement('a');
      fileLink.href = fileURL;
      fileLink.setAttribute('download', `invoice_${userProjectId}.pdf`);
      document.body.appendChild(fileLink);
      fileLink.click();
      fileLink.remove();
    })
    .catch((err) => console.error(err));
  };

  return (
    <div className="page-container invoice-container">
      <div className="hero-content">
        <h1>Generate Your Invoices</h1>
        <p>Convert your tracked hours into professional invoices easily.</p>
      </div>
      <div className="invoice-content">
        {completedProjects.length === 0 ? (
          <p>No completed projects to invoice yet.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Project Name</th>
                <th>Total Amount</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {completedProjects.map((project) => (
                <tr key={project.id}>
                  <td>{project.projectName}</td>
                  <td>${project.totalAmount.toFixed(2)}</td>
                  <td>
                    <button onClick={() => handleDownload(project.id)}>
                      Download PDF
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
