import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/YourProjects.css";

export default function YourProjects() {
  const [userProjects, setUserProjects] = useState([]);
  const username = localStorage.getItem("username") || "guest";

  // Fetch all projects selected by the user
  useEffect(() => {
    if (!username) return;

    axios
      .get(`http://localhost:8080/api/user-projects?username=${username}`)
      .then((res) => setUserProjects(res.data))
      .catch((err) => console.error("Error fetching user projects:", err));
  }, [username]);

  // End a project
  const handleEndProject = (up) => {
    axios
      .post(
        `http://localhost:8080/api/user-projects/end?username=${username}&projectId=${up.projectId}&pricePerHour=${up.pricePerHour}`
      )
      .then((res) => {
        alert(`Project ended! Total earned: $${res.data.totalAmount.toFixed(2)}`);
        // Update the project in state
        setUserProjects((prev) =>
          prev.map((p) => (p.id === res.data.id ? res.data : p))
        );
      })
      .catch((err) => console.error("Error ending project:", err));
  };

  // Download invoice PDF
  const handleDownloadInvoice = (up) => {
    window.open(`http://localhost:8080/api/invoice/${up.id}`);
  };

  return (
    <div className="page-container your-projects-container">
      <h2>Your Projects</h2>
      {userProjects.length === 0 && <p>No projects started yet.</p>}

      {userProjects.map((up) => (
        <div key={up.id} className="user-project-card">
          <p>Project ID: {up.projectId}</p>
          <p>Start: {up.startTime}</p>
          <p>End: {up.endTime || "In progress"}</p>
          <p>Total Earned: ${up.totalAmount || 0}</p>

          {!up.endTime && (
            <button onClick={() => handleEndProject(up)}>End Project</button>
          )}
          {up.endTime && (
            <button onClick={() => handleDownloadInvoice(up)}>
              Download Invoice
            </button>
          )}
        </div>
      ))}
    </div>
  );
}
