import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import Login from "./pages/Login";
import Dashboard from "./pages/Dashboard";
import Projects from "./pages/Projects";
import TimerPage from "./pages/TimerPage";
import InvoicePage from "./pages/InvoicePage";
import Reports from "./pages/Reports";
import Profile from "./pages/Profile";
import Navbar from "./components/Navbar";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // clicked Get Started
  const [hasDashboardAccess, setHasDashboardAccess] = useState(false); // entered credentials

  useEffect(() => {
    const storedLogin = localStorage.getItem("isLoggedIn") === "true";
    setIsLoggedIn(storedLogin);

    const storedAccess = localStorage.getItem("hasDashboardAccess") === "true";
    setHasDashboardAccess(storedAccess);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("hasDashboardAccess");
    setIsLoggedIn(false);
    setHasDashboardAccess(false);
  };

  return (
    <Router>
      {/* Navbar only shows if dashboard credentials entered */}
      {hasDashboardAccess && <Navbar onLogout={handleLogout} />}

      <Routes>
        <Route
          path="/"
          element={
            !isLoggedIn ? (
              <Login setIsLoggedIn={setIsLoggedIn} />
            ) : !hasDashboardAccess ? (
              <Navigate to="/dashboard" replace />
            ) : (
              <Navigate to="/dashboard" />
            )
          }
        />

        <Route
          path="/dashboard"
          element={
            isLoggedIn ? (
              <Dashboard
                hasDashboardAccess={hasDashboardAccess}
                setHasDashboardAccess={setHasDashboardAccess}
              />
            ) : (
              <Navigate to="/" />
            )
          }
        />
        <Route
          path="/projects"
          element={hasDashboardAccess ? <Projects /> : <Navigate to="/dashboard" />}
        />
        <Route
          path="/timer"
          element={hasDashboardAccess ? <TimerPage /> : <Navigate to="/dashboard" />}
        />
        <Route
          path="/invoice"
          element={hasDashboardAccess ? <InvoicePage /> : <Navigate to="/dashboard" />}
        />
        <Route
          path="/reports"
          element={hasDashboardAccess ? <Reports /> : <Navigate to="/dashboard" />}
        />
        <Route
          path="/profile"
          element={hasDashboardAccess ? <Profile /> : <Navigate to="/dashboard" />}
        />

        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
