import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

// Pages
import Login from "./pages/Login";
import Dashboard from "./pages/Dashboard";
import Projects from "./pages/Projects";
import TimerPage from "./pages/TimerPage";
import InvoicePage from "./pages/InvoicePage";
import Reports from "./pages/Reports";
import Profile from "./pages/Profile";

// Components
import Navbar from "./components/Navbar";

// Styles
import "./styles/styles.css";
import "./styles/Login.css";
import "./styles/Dashboard.css";
import "./styles/Projects.css";
import "./styles/TimerPage.css";
import "./styles/InvoicePage.css";
import "./styles/Navbar.css";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const storedLogin = localStorage.getItem("isLoggedIn") === "true";
    setIsLoggedIn(storedLogin);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    setIsLoggedIn(false);
  };

  return (
    <Router>
      {/* Navbar only shows if logged in */}
      {isLoggedIn && <Navbar onLogout={handleLogout} />}

      <Routes>
        {/* Login page */}
        <Route
          path="/"
          element={<Login setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} />}
        />

        {/* Protected pages */}
        <Route
          path="/dashboard"
          element={isLoggedIn ? <Dashboard /> : <Navigate to="/" />}
        />
        <Route
          path="/projects"
          element={isLoggedIn ? <Projects /> : <Navigate to="/" />}
        />
        <Route
          path="/timer"
          element={isLoggedIn ? <TimerPage /> : <Navigate to="/" />}
        />
        <Route
          path="/invoice"
          element={isLoggedIn ? <InvoicePage /> : <Navigate to="/" />}
        />
        <Route
          path="/reports"
          element={isLoggedIn ? <Reports /> : <Navigate to="/" />}
        />
        <Route
          path="/profile"
          element={isLoggedIn ? <Profile /> : <Navigate to="/" />}
        />

        {/* Fallback */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
