import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

// Pages
import Dashboard from "./pages/Dashboard";
import Projects from "./pages/Projects";
import TimerPage from "./pages/TimerPage";
import InvoicePage from "./pages/InvoicePage";
import Reports from "./pages/Reports";
import Profile from "./pages/Profile";

// Components
import Navbar from "./components/Navbar";

// Global styles
import "./styles/styles.css";
import "./styles/Dashboard.css";
import "./styles/Projects.css";
import "./styles/TimerPage.css";
import "./styles/InvoicePage.css";
import "./styles/Navbar.css";

function App() {
  // Tracks if the user has entered credentials in the Dashboard
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Optional: persist login on page reload
  useEffect(() => {
    const storedLogin = localStorage.getItem("isLoggedIn") === "true";
    setIsLoggedIn(storedLogin);
  }, []);

  // Called by Dashboard when credentials are entered
  const handleDashboardLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem("isLoggedIn", "true");
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem("isLoggedIn");
  };

  return (
    <Router>
      {/* Show Navbar only if user entered credentials */}
      {isLoggedIn && <Navbar onLogout={handleLogout} />}

      <Routes>
        {/* Dashboard page with credentials form */}
        <Route
          path="/dashboard"
          element={<Dashboard onLogin={handleDashboardLogin} />}
        />

        {/* Protected routes: accessible only after entering credentials */}
        <Route
          path="/projects"
          element={isLoggedIn ? <Projects /> : <Navigate to="/dashboard" />}
        />
        <Route
          path="/timer"
          element={isLoggedIn ? <TimerPage /> : <Navigate to="/dashboard" />}
        />
        <Route
          path="/invoice"
          element={isLoggedIn ? <InvoicePage /> : <Navigate to="/dashboard" />}
        />
        <Route
          path="/reports"
          element={isLoggedIn ? <Reports /> : <Navigate to="/dashboard" />}
        />
        <Route
          path="/profile"
          element={isLoggedIn ? <Profile /> : <Navigate to="/dashboard" />}
        />

        {/* Redirect unknown paths to Dashboard */}
        <Route path="*" element={<Navigate to="/dashboard" />} />
      </Routes>
    </Router>
  );
}

export default App;
