import React, { useState } from "react";
import "../styles/Dashboard.css";

export default function Dashboard({ hasDashboardAccess, setHasDashboardAccess }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username.trim() && password.trim()) {
      setHasDashboardAccess(true);
      localStorage.setItem("hasDashboardAccess", "true");
    } else {
      alert("Enter username and password!");
    }
  };

  return (
    <div className="dashboard-container">
      <div className="overlay">
        <div className="dashboard-card">
          {!hasDashboardAccess ? (
            <form className="credentials-form" onSubmit={handleSubmit}>
              <h1>Enter Credentials</h1>
              <div className="form-group">
                <label>Username</label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Enter username"
                />
              </div>
              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter password"
                />
              </div>
              <button type="submit" className="btn-submit">
                Enter
              </button>
            </form>
          ) : (
            <div>
              <h1>Welcome Back, Freelancer!</h1>
              <p>Track your time, manage your projects, and see your earnings grow.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
