import React, { useEffect, useState } from "react";
import axios from "axios";

export default function YourProjects() {
  const [userProjects, setUserProjects] = useState([]);
  const username = localStorage.getItem("username") || "guest";

  useEffect(() => {
    // Fetch project details for the user from the updated endpoint
    axios
      .get(`http://localhost:8081/api/user-projects?username=${username}`)
      .then((res) => setUserProjects(res.data))
      .catch((err) => console.error(err));
  }, [username]);

  const handleComplete = (id) => {
    const pricePerHour = 20; // example, you can change dynamically
    axios
      .post(`http://localhost:8081/api/projects/complete`, null, {
        params: { userProjectId: id, pricePerHour },
      })
      .then((res) => {
        // After completing the project, refetch the list to update the status and amount
        axios.get(`http://localhost:8081/api/user-projects?username=${username}`)
          .then((res) => setUserProjects(res.data))
          .catch((err) => console.error(err));
      })
      .catch((err) => console.error(err));
  };

  return (
    <div className="page-container">
      <h1>Your Projects</h1>
      {userProjects.length === 0 ? (
        <p>No active projects yet.</p>
      ) : (
        <div className="projects-grid">
          {userProjects.map((up) => (
            <div className="user-project-card" key={up.id}>
              <div className="project-info">
                {/* IMPORTANT CHANGE: 
                  Displaying the project name from the backend.
                */}
                <p><strong>Project:</strong> {up.projectName}</p>
                <p><strong>Started:</strong> {new Date(up.startTime).toLocaleString()}</p>
                <p>
                  <strong>Status:</strong>{" "}
                  {up.endTime ? "Completed" : "In Progress"}
                </p>
                {/* Show total amount only if the project is completed */}
                {up.endTime && <p><strong>Total Amount:</strong> ${up.totalAmount.toFixed(2)}</p>}
              </div>
              {!up.endTime && (
                <button onClick={() => handleComplete(up.id)}>Complete</button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
