// src/pages/Login.jsx
import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Login.css"; // ðŸ‘ˆ add styles

function Login() {
  const navigate = useNavigate();

  const handleLogin = () => {
    localStorage.setItem("isLoggedIn", "true");
    navigate("/dashboard");
  };

  return (
    <div className="login-container">
      <div className="overlay">
        <div className="hero-content">
          <h1>Are you free now?</h1>
          <p>
            Do you want to take on exciting projects, track your work time, and generate invoices easily?
            <br />
            Choose your project, start working, and earn your way!
          </p>
          <button className="login-btn" onClick={handleLogin}>
            Get Started
          </button>
        </div>
      </div>
    </div>
  );
}

export default Login;
