// src/pages/Dashboard.jsx
import React, { useState } from "react";
import "../styles/Dashboard.css";

export default function Dashboard({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    if (username.trim() && password.trim()) {
      setIsAuthenticated(true);
      onLogin(); // notify App.js that user is authenticated
    } else {
      alert("Please enter username and password!");
    }
  };

  return (
    <div className="dashboard-container">
      <div className="overlay">
        <div className="dashboard-card">
          {!isAuthenticated ? (
            <form onSubmit={handleLogin} className="credentials-form">
              <h1>Enter Credentials to Access Dashboard</h1>
              <div className="form-group">
                <label>Username</label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Enter username"
                />
              </div>
              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter password"
                />
              </div>
              <button type="submit" className="btn-submit">
                Enter
              </button>
            </form>
          ) : (
            <div>
              <h1>Welcome Back, Freelancer!</h1>
              <p>
                Track your time, manage your projects, and see your earnings grow.
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
